import React, { Component } from 'react';
import {Tabs, Tab, Grid, Cell, Card, CardActions, Button, CardTitle,CardMenu, IconButton, CardText, Icon} from 'react-mdl';
class projects extends Component{
    constructor (props){
        super(props);
        this.state={ activeTab: 0};
    }
    toggleCategories(){
        if(this.state.activeTab === 0){
            return(
                <Card shadow={5} style={{minWidth: '450', margin: 'auto'}}>
                    <CardTitle style={{color: '#fff', height: '176px', background: 'url(/home/kacha_nimbu/Code/my_website/mywebsite/img/thumbnails/recosys.jpg) center / cover'}}>Project #1</CardTitle>
                    <CardText>
                        Dummy Text1
                    </CardText>
                    <CardActions border>
                        <div className="project-links">
                            <a href="https://github.com/abhineet99" rel="noopener noreferrer" target="_blank">
                                <i className="fa fa-github-square"  aria-hidden="true"/>
                            </a>
                        </div>
                    </CardActions>
                    <CardMenu style={{color: '#ffff'}}>
                        <Icon name="share"/>
                    </CardMenu>
                </Card>
            )

        }
    }
    render(){
        return(
         <div className="category-tabs">
            <Tabs activeTab={this.state.activeTab} onChange={(tabId)=> this.setState({activeTab: tabId})} ripple>
                <Tab>Tab1</Tab>
                <Tab>Tab2</Tab>
            </Tabs>
            <section className="projects-grid">
                <Grid className="projects-grid">
                    <Cell col={12}>
                        <div className="content">{this.toggleCategories()}</div>
                    </Cell>
                </Grid>                
            </section>
         </div>
        )
    }
}

export default projects;
